import React from 'react';
import {Form, Input, Button, Select, DatePicker} from 'antd';

const {Option} = Select;

let idPais = '';

class Paso2 extends React.Component {

    constructor(props) {
        super(props);

        this.state = {
            nacionalidades: [],
            ciudades: [],
            provincias: [],
            distritos: [],
            fichaPersona: [],
        };
    }

    componentDidMount() {
        this.getPaises();
        this.getFichaPersona();
    }

    getFichaPersona() {
        fetch('http://localhost/FichaWeb/app/controller/fichaPersona/read.php')
            .then(response => response.json())
            .then(data => this.setState({fichaPersona: data}))
    }

    getPaises() {
        fetch('http://localhost/FichaWeb/app/controller/nacionalidad/read.php')
            .then(response => response.json())
            .then(data => this.setState({nacionalidades: data}));
    }

    getCiudades() {
        fetch(`http://localhost/FichaWeb/app/controller/ciudad/read.php?idPais=${idPais}`)
            .then(response => response.json())
            .then(data => this.setState({ciudades: data}));
        console.log(idPais);
    }

    getProvincias() {

    }

    getDistritos() {

    }

    render() {

        const {nacionalidades} = this.state;
        const {ciudades} = this.state;
        const {fichaPersona} = this.state;

        const formItemLayout = {
            labelCol: {
                xs: {span: 24},
                sm: {span: 8},
            },
            wrapperCol: {
                xs: {span: 24},
                sm: {span: 16},
            },
        };

        const {getFieldDecorator} = this.props.form;
        const config = {
            rules: [{type: 'object', required: true, message: 'Por favor seleccione la fecha de su nacimiento'}],
        };

        function handleChanges(value) {
            console.log(`se ha seleccionado ${value}`);
        }

        return (
            <Form {...formItemLayout} style={{width: "75%"}}>
                <Form.Item label="Fecha de Nacimiento">
                    {getFieldDecorator('fechaNac',config)
                    (
                        <DatePicker defaultPickerValue={fichaPersona.fch_nacimiento_per} placeholder={"25/11/1999"} format={'DD-MM-YYYY'}/>
                    )}
                </Form.Item>

                <Form.Item label="País">
                    {getFieldDecorator('pais', {
                        initialValue: fichaPersona.id_nac_pais_per,
                        rules: [
                            {
                                required: true,
                                message: 'Por favor seleccione el país.',
                            },
                        ],
                    })(
                        <Select
                            placeholder={"Perú"}
                            onChange={handleChanges}
                            showSearch={true}
                            optionFilterProp={"children"}
                            filterOption={(input, option) =>
                                option.props.children.toLowerCase().indexOf(input.toLowerCase()) >= 0
                            }
                        >
                            {
                                nacionalidades.map(nacionalidad =>
                                    <Option value={nacionalidad.CodNac}>{nacionalidad.Descripcion}</Option>
                                )
                            }
                        </Select>)}
                </Form.Item>
                <Form.Item label="Ciudad">
                    {getFieldDecorator('ciudad', {
                        initialValue: fichaPersona.id_nac_ciudad_per,
                        rules: [
                            {
                                required: true,
                                message: 'Por favor seleccione la ciudad.',
                            },
                        ],
                    })(
                        <Select placeholder={"Seleccione ciudad"} onChange={handleChanges}>
                            {
                                ciudades.map(ciudad =>
                                    <Option value={ciudad.CodCiudad}>{ciudad.descripcion}</Option>)
                            }
                        </Select>)}
                </Form.Item>
                {/*<Form.Item label="Provincia">*/}
                {/*    {getFieldDecorator('provincia', {*/}
                {/*        rules: [*/}
                {/*            {*/}
                {/*                required: true,*/}
                {/*                message: 'Por favor seleccione la provincia.',*/}
                {/*            },*/}
                {/*        ],*/}
                {/*    })(*/}
                {/*        <Select placeholder={"DNI"} onChange={handleChanges}>*/}
                {/*            {tiposDocumentos.map(tipoDocumento =>*/}
                {/*                <Option value={tipoDocumento.id_tipo_doc}>{tipoDocumento.desc_tipo_doc}</Option>*/}
                {/*            )}*/}
                {/*        </Select>)}*/}
                {/*</Form.Item>*/}
                {/*<Form.Item label="Distrito">*/}
                {/*    {getFieldDecorator('distrito', {*/}
                {/*        rules: [*/}
                {/*            {*/}
                {/*                required: true,*/}
                {/*                message: 'Por favor seleccione el distrito.',*/}
                {/*            },*/}
                {/*        ],*/}
                {/*    })(<Input/>)}*/}
                {/*</Form.Item>*/}
            </Form>
        )
    }

}

export default Form.create()(Paso2);
