import React from 'react';
import DatosPersonales from "./DatosPersonales";
import LugarFechaNacimiento from "./LugarFechaNacimiento";
import LugarResidenciaActual from "./LugarResidenciaActual";
import NivelEducativo from "./NivelEducativo";
import {Steps, Button, Icon, Modal, Input, Form} from "antd";

const {Step} = Steps;

class UsuarioForm extends React.Component {

  constructor(props) {
    super(props);
    this.state = {
      paso: 0,
      nombre_per: '',
      apellido_pat_per: '',
      apellido_mat_per: '',
      id_tipo_doc: '',
      nro_doc_per: '',
      sexo_per: '',
      id_est_civil: '',
      telefono_per: '',
      celular_per: '',
      email_per: '',
      nacionalidad: '',
      fch_nacimiento_per: '',
      id_nac_pais_per: '',
      id_nac_ciudad_per: '',
      id_nac_provincia_per: '',
      id_nac_distrito_per: '',
      residencia_per: '',
      id_res_ciudad_per: '',
      id_res_provincia_per: '',
      id_res_distrito_per: '',
      res_direccion_per: '',
      res_referencia_per: '',
      res_telefono_per: '',
      id_idioma_origen: '',
      idioma_ingles_per: '',
      idioma_portuguez_per: '',
      idioma_frances_per: '',
      id_idioma_otro: '',
      IdAdm_Discapacidad: '',
      idIEdu: '',
      id_educ_pais_per: '',
      id_educ_ciudad_per: '',
      id_educ_provincia_per: '',
      id_educ_distrito_per: '',

      loading: false,
      bloqued: false,
      visibleSave: true,
      visibleModal: false,
      visibleSend: false,
      disableNumero: false
    }
  }

  siguiente() {
    const paso = this.state.paso + 1;
    this.setState({
      paso
    });

  };

  regresar() {
    const paso = this.state.paso - 1;
    this.setState({
      paso
    });
  };

  handleChangeInput = input => (e) => {
    this.setState({[input]: e.target.value});
  };

  handleChangeSelect = select => (e) => {
    this.setState({[select]: e});
  };

  handleChangeDatePicker = picker => (date, dateString) => {
    this.setState({[picker]: date});
    console.log(this.state.fch_nacimiento_per);
  }

  handleOk() {
    this.setState({loading: true, bloqued: true, disableNumero: true});
    setTimeout(() => {
      this.setState({loading: false, visibleSend: true, visibleSave: false});
    }, 2000);
  }

  handleCancel = () => {
    this.setState({visibleModal: false, visibleSend: false, visibleSave: true, disableNumero: false});
  };

  handleSave = () => {
    this.setState({visibleSave: false, visibleSend: true});
  };

  showModal = () => {
    this.setState({
      visibleModal: true,
    });
  };

  getFichaPersona() {
    fetch('http://localhost/FichaWeb/app/controller/fichaPersona/read.php')
      .then(response => response.json())
      .then(data => this.setState({
        nombre_per: data.NomPer,
        apellido_pat_per: data.ApepPer,
        apellido_mat_per: data.ApemPer,
        id_tipo_doc: data.TipoDocum,
        nro_doc_per: data.DniPer,
        sexo_per: data.Sexo,
        telefono_per: data.TelefFijo,
        celular_per: data.TelefCelular,
        email_per: data.Email,
        fch_nacimiento_per: data.FechaNac,
        res_direccion_per: data.Direccion,
        id_educ_pais_per: data.id_educ_pais_per,
        id_educ_ciudad_per: data.id_educ_ciudad_per,
        id_educ_provincia_per: data.id_educ_provincia_per,
        id_educ_distrito_per: data.id_educ_distrito_per,
        idIEdu: data.IdiEdu
      }))
  }

  componentDidMount() {
    this.getFichaPersona();
  }


  render() {
    const {paso, loading, visibleModal, bloqued, visibleSave, visibleSend, disableNumero} = this.state;
    const {
      nombre_per, apellido_pat_per, apellido_mat_per, id_tipo_doc, nro_doc_per, sexo_per, id_est_civil,
      telefono_per, celular_per, email_per, nacionalidad, fch_nacimiento_per, id_nac_pais_per, id_nac_ciudad_per,
      id_nac_provincia_per, id_nac_distrito_per, residencia_per, id_res_ciudad_per, id_res_provincia_per, id_res_distrito_per,
      res_direccion_per, res_referencia_per, res_telefono_per, id_idioma_origen, idioma_ingles_per, idioma_portuguez_per,
      idioma_frances_per, id_idioma_otro, IdAdm_Discapacidad, idIEdu, id_educ_pais_per, id_educ_ciudad_per, id_educ_provincia_per, id_educ_distrito_per
    } = this.state;
    const ficha = {
        nombre_per,
        apellido_pat_per,
        apellido_mat_per,
        id_tipo_doc,
        nro_doc_per,
        sexo_per,
        id_est_civil,
        telefono_per,
        celular_per,
        email_per,
        nacionalidad,
        fch_nacimiento_per,
        id_nac_pais_per,
        id_nac_ciudad_per,
        id_nac_provincia_per,
        id_nac_distrito_per,
        residencia_per,
        id_res_ciudad_per,
        id_res_provincia_per,
        id_res_distrito_per,
        res_direccion_per,
        res_referencia_per,
        res_telefono_per,
        id_idioma_origen,
        idioma_ingles_per,
        idioma_portuguez_per,
        idioma_frances_per,
        id_idioma_otro,
        IdAdm_Discapacidad,
        idIEdu,
        id_educ_pais_per,
        id_educ_ciudad_per,
        id_educ_provincia_per,
        id_educ_distrito_per
      }
    ;

    const pasos = [
      {
        title: 'Datos Personales',
        content: <DatosPersonales ficha={ficha}
                                  handleChangeInput={this.handleChangeInput}
                                  handleChangeSelect={this.handleChangeSelect}/>
      },
      {
        title: 'Lugar de y fecha de nacimiento',
        content: <LugarFechaNacimiento ficha={ficha}
                                       handleChangeInput={this.handleChangeInput}
                                       handleChangeSelect={this.handleChangeSelect}
                                       handleChangeDatePicker={this.handleChangeDatePicker}/>
      },
      {
        title: 'Lugar de residencia actual',
        content: <LugarResidenciaActual ficha={ficha}
                                        handleChangeInput={this.handleChangeInput}
                                        handleChangeSelect={this.handleChangeSelect}/>
      },
      {
        title: 'Nivel educativo e institucion educativa',
        content: <NivelEducativo ficha={ficha}
                                 handleChangeInput={this.handleChangeInput}
                                 handleChangeSelect={this.handleChangeSelect}/>
      },
    ]

    const formItemLayout = {
      labelCol: {
        xs: {span: 24},
        sm: {span: 8},
      },
      wrapperCol: {
        xs: {span: 24},
        sm: {span: 16},
      },
    };

    const {getFieldDecorator} = this.props.form;

    return (
      <div>
        <Steps current={paso}>
          {pasos.map(item => (
            <Step key={item.title} title={item.title}/>
          ))}
        </Steps>
        <div className="steps-content">{pasos[paso].content}</div>
        <div className="steps-action">
          {paso > 0 && (
            <Button style={{marginLeft: 8}} onClick={() => this.regresar()} icon={"caret-left"}>
              Regresar
            </Button>
          )}
          {paso < pasos.length - 1 && (
            <Button type="primary" onClick={() => this.siguiente()} icon={"caret-right"}>
              Siguiente
            </Button>
          )}
          {paso === pasos.length - 1 && (
            <Button type="primary" icon={"save"} onClick={() => this.showModal()}>
              Guardar
            </Button>
          )}
          <Modal
            width={500}
            visible={visibleModal}
            style={{textAlign: 'inherit'}}
            title="Confirmación por SMS"
            onOk={this.handleOk}
            onCancel={this.handleCancel}
            centered={true}
            footer={[
              <div style={{textAlign: "center"}}>
                <Button style={{width: '50%', marginBottom: '8px'}} key="back" icon={"close"} type="danger"
                        onClick={() => this.handleCancel()}>
                  Cancelar
                </Button>
              </div>,
              <div style={{textAlign: "center"}}>
                <Button hidden={visibleSend} style={{width: '50%', marginBottom: '8px'}} key="submit" icon={"check"}
                        type="primary" loading={loading}
                        block={bloqued} onClick={() => this.handleOk()}>
                  Enviar
                </Button>
              </div>,
              <div style={{textAlign: "center"}}>
                <Button hidden={visibleSave} style={{width: '50%'}} key="submit" type="primary" loading={loading}
                        onClick={() => this.handleSave()}>
                  Confirmar y Guardar
                </Button>
              </div>
            ]}>

            <Form {...formItemLayout}>
              <Form.Item label="Numero de celular">
                {getFieldDecorator('celular', {
                  initialValue: celular_per,
                  rules: [
                    {
                      required: true,
                      message: 'Por favor ingrese su número de celular',
                    },
                  ],
                })(<Input disabled={disableNumero} onChange={this.handleChangeInput('celular_per')}
                          style={{width: '45%'}}/>)}
              </Form.Item>
              <Form.Item label="Mensaje de confirmación">
                {getFieldDecorator('confirmacion', {
                  rules: [
                    {
                      required: true,
                      message: 'Por favor ingrese el código de confirmación',
                    },
                  ],
                })(<Input style={{width: '45%'}}/>)}
              </Form.Item>
            </Form>
          </Modal>
        </div>
      </div>
    )
  }
}

export default Form.create()(UsuarioForm);
